INSERT INTO `` (`id`,`category_id`,`name`,`slug`,`illustration`,`subtitle`,`description`,`price`,`is_best`) VALUES (28,8,'W- 002','w-002-baie-vitree','dcd09ad06636d57fb01b31dab68fd0a29f892073.png',NULL,'Intègre plus de rails permettant l’intégration de nombreux vantaux coulissants supplémentaires.\r\nLaissez place à vos envies ! Le CP 68 combine de nombreuses fonctionnalités... coulissant universel, à galandage ou encore d’angle.',NULL,0);
